<?php

namespace App\Console\Commands;

use App\Models\Prices;
use App\Models\Products;
use Illuminate\Console\Command;
use Illuminate\Support\Facades\Log;
use Illuminate\Support\Facades\Http;

class CrawlLazadaProducts extends Command
{
    /**
     * The name and signature of the console command.
     *
     * @var string
     */
    protected $signature = 'crawl:lazada-products';

    /**
     * The console command description.
     *
     * @var string
     */
    protected $description = 'Crawl product from Lazada';

    public const LAST_LAZADA_PAGE = 25;

    /**
     * Execute the console command.
     */
    public function handle()
    {

        for ($i = 1; $i <= self::LAST_LAZADA_PAGE; $i++) {
            // Make GET request to the URL
            $response = Http::get('https://www.lazada.vn/locklock-flagship-store/?ajax=true&from=wangpu&isFirstRequest=true&langFlag=vi&pageTypeId=2&q=All-Products&style=wf&page='.$i);

            if ($response->successful()) {
                $responseData = $response->body();

                //$responseData = $this->mockHtml();

                // Decode response JSON string into PHP associative array
                $responseArray = json_decode($responseData, true);

                // Check if decoding was successful
                if ($responseArray === null) {
                    echo "Failed to decode JSON.";
                    Log::error('crawlFailed', ['error_msg' => 'Failed to decode JSON on page:' . $i]);
                } else {
                    // Access the listItems array inside the mods array
                    $listItems = $responseArray['mods']['listItems'];

                    // Loop through listItems array and get the name
                    foreach ($listItems as $key => $item) {
                        $title = $item['name'];
                        $price = $item['price'];
                        $originalPrice = $item['originalPrice'] ?? 0;
                        $discount = $this->extractNumbers($item['discount'] ?? 0);
                        $sold = $item['itemSoldCntShow'] ?? 0;
                        $location = $item['location'];
                        $imgSrc = $item['image'];
                        $rating = $item['ratingScore'] ?? 0;

                        // Insert into Product + Price table
                        $productModel = new Products();
                        $productModel->title = $title;
                        $productModel->location = $location;
                        $productModel->image = $imgSrc;
                        $productModel->rating = $rating;
                        $productModel->save();

                        $priceModel = new Prices();
                        $priceModel->original_price = $originalPrice;
                        $priceModel->price = $price;
                        $priceModel->discount = $discount;
                        $priceModel->sold = $sold;

                        $productModel->prices()->save($priceModel);

                    }
                }
                echo "Finish crawl page:" . $i . "\r\n";
            } else {
                Log::error('requestFailed', ['error_msg' => $response->message()]);
            }
            sleep(5);
        }
    }

    public function extractNumbers($string)
    {
        // Remove any characters that are not digits or periods
        $cleanedString = preg_replace('/[^0-9.]/', '', $string);

        // Remove any leading or trailing periods
        $cleanedString = trim($cleanedString, '.');

        // Remove any additional periods (except the first one)
        $cleanedString = preg_replace('/\.(?=.*\.)/', '', $cleanedString);

        return $cleanedString;
    }
